import mcpi.minecraft as minecraftimport mcpi.block as block# Connect to the Minecraft gamemc = minecraft.Minecraft.create()# Get the player positionpos = mc.player.getTilePos()# Ask the user how big a space to clear#size = int(input("size of area to clear? "))# Clear a space size by size*size*size, by setting it to AIRsize = 50mc.setBlocks(pos.x+3, pos.y, pos.z, pos.x+3+size, pos.y+size, pos.z+size, block.AIR.id)